import React, { useState } from "react";
import {
  Container,
  Col,
  Row,
  Badge,
  Button,
  Navbar,
  Nav,
} from "react-bootstrap";
import Typewriter from "typewriter-effect";
import UploadPage from "./UploadPage";
import Chatbot from "./chatComponent/ChatbotPage";

const results = {
  genres: {
    Action: 10,
    Comedy: 120,
    Fantasy: 20,
    Animation: 10,
    Drama: 10,
    Crime: 30,
  },
  views: {
    Friends: 300,
    "Shutter Island": 10,
    "Criminal Minds": 100,
    "Sherlock Holmes": 200,
    "Pans Labyrinth": 11,
    "The Witcher": 20,
    "The Croods": 1,
    "Breaking Bad": 50,
  },
  viewcount: {
    "2020-01": 120,
    "2020-02": 300,
    "2020-03": 100,
    "2020-04": 90,
    "2020-05": 40,
    "2020-06": 50,
    "2020-07": 160,
    "2020-08": 80,
    "2020-09": 260,
    "2020-10": 150,
    "2020-11": 290,
    "2020-12": 390,
    "2021-01": 200,
    "2021-02": 120,
    "2021-03": 180,
  },
  runtime: {
    data: {
      "2020-01": 3055,
      "2020-02": 2405,
      "2020-03": 3840,
      "2020-04": 2500,
      "2020-05": 3343,
      "2020-06": 2854,
      "2020-07": 4200,
      "2020-08": 2900,
      "2020-09": 2570,
      "2020-10": 2700,
      "2020-11": 2999,
      "2020-12": 2400,
      "2021-01": 3300,
      "2021-02": 4050,
      "2021-03": 4500,
    },
  },
  popularity: { percents: [20, 70, 10], score: 23, topShow: "Friends" },
};

const Sample = () => {
  const [doneTyping, setTypingDone] = useState(false);
  const [yesButton, setYesButton] = useState(false);
  const handleDone = () => {
    setTypingDone(true);
  };
  const handleYesButton = () => {
    setYesButton(true);
  };
  return (
    <div>
      {yesButton ? (
        <UploadPage />
      ) : (
        <div>
          <Navbar
            // fixed="sticky"
            style={{ position: "absolute", top: 10, left: 80 }}
            // className="justify-content-center mb-5"
          >
            <Navbar.Brand>
              <h1 className="netflix-red">
                Your Netflix Analysis <Badge variant="secondary">Sample</Badge>
              </h1>
            </Navbar.Brand>
          </Navbar>
          {doneTyping ? (
            <Chatbot result={results} />
          ) : (
            <Typewriter
              onInit={(typewriter) => {
                typewriter
                  .typeString("Analyzing sample data...")
                  .pauseFor(2500)
                  .deleteAll()
                  .typeString("Generating the score...")
                  .pauseFor(2500)
                  .deleteAll()
                  .typeString("Done.")
                  .pauseFor(2500)
                  .callFunction(() => {
                    handleDone();
                  })
                  .start();
              }}
              options={{
                delay: 50,
                deleteSpeed: 30,
              }}
            />
          )}
          <Navbar
            sticky="bottom"
            style={{ position: "absolute", bottom: 20, right: 30 }}
            className="justify-content-end mt-5"
          >
            <Nav>
              <h1 style={{ fontSize: 18, marginTop: "5px" }}>
                Ready to try with your own data?
              </h1>
              <Button
                variant="outline-light"
                onClick={handleYesButton}
                className="ml-3"
                style={{ fontSize: 15 }}
              >
                Let's get started
              </Button>
            </Nav>
          </Navbar>
        </div>
      )}
    </div>
  );
};
export default Sample;
