import React from "react";
import { Card, Container } from "react-bootstrap";
import YouTube from "react-youtube";

//Instruction Youtube link & ID:
//ID: 4PUr9vmyaLo
//URL: https://youtu.be/4PUr9vmyaLo

const Instruction = () => {
  const VideoOnReady = (event) => {
    event.target.stopVideo();
  };

  const videoOptions = {
    height: "390",
    width: "800",
    playerVars: {
      autoplay: 1,
    },
  };

  return (
    <Container className="text-center">
      <p>Rendering Instruction Page</p>
      <Card className="mb-5">
        <Card.Body>
          <Card.Text className="text-dark text-left">
            <ol>
              <li>
                Go to your{" "}
                <a href="https://www.netflix.com/YourAccount">
                  Netflix Account{" "}
                </a>
                page.
              </li>
              <li>
                Select the profile you want to see and open Viewing Activity
              </li>
              <li>Click "Download All" to get csv file</li>
              <li>Save the csv file and upload below</li>
            </ol>
          </Card.Text>
          <YouTube
            videoId="4PUr9vmyaLo"
            opts={videoOptions}
            onReady={VideoOnReady}
          />{" "}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default Instruction;
