import React, { useState } from "react";
import { Button, Card, Container, Row, Col } from "react-bootstrap";
import UploadPage from "./UploadPage";

const Instruction = () => {
  const [instruction, setInstruction] = useState(false);
  const [sample, setSample] = useState(false);
  const [noButtonDisplay, setNoButtonDisplay] = useState("inline");
  const [okButtonDisplay, setOkButtonDisplay] = useState("inline");
  const [okButtonColor, setOkButtonColor] = useState("light");
  const [noButtonColor, setNoButtonColor] = useState("light");

  const handleOkButton = () => {
    setInstruction(true);
    setNoButtonDisplay("none");
    setOkButtonColor("dark");
  };

  const handleSampleButton = () => {
    setSample((prevProp) => !prevProp);
    setOkButtonDisplay("none");
    setNoButtonColor("dark");
  };

  return (
    <Container className="text-right">
      <Row>
        <p>
          To get started, I will need to see your Netflix viewing history. Don't
          worry, I will just take a look at what you watched. I won't post or
          change anything.
        </p>
      </Row>
      <Row className="mb-3">
        <Col>
          <Button
            variant={okButtonColor}
            onClick={handleOkButton}
            style={{ display: okButtonDisplay }}
          >
            Got it
          </Button>
        </Col>
      </Row>
      <Row className="mb-4">
        <Col>
          <Button
            variant={noButtonColor}
            onClick={handleSampleButton}
            style={{ display: noButtonDisplay }}
          >
            No I don't want to share my view history
          </Button>
        </Col>
      </Row>
      {sample && (
        <div>
          <p>
            I can't judge your Netflix without seeing your view history. I guess
            you can see how great our analysis could be using our sample
            data.....
          </p>
          <Button>Show me sample analysis</Button>
        </div>
      )}
      {instruction && (
        <div>
          <Card>
            <Card.Body>
              <Card.Text className="text-dark">
                <ol>
                  <li>
                    From a web browser, go to your
                    <a href="https://www.netflix.com/YourAccount">
                      {" "}
                      Account{" "}
                    </a>{" "}
                    page.
                  </li>
                  <li>
                    Open the Profile & Parental Controls settings for the
                    profile you want to see.
                  </li>
                  <li>Open Viewing activity. </li>
                  <li>If you see a limited list, use the Show More button.</li>
                </ol>
              </Card.Text>
            </Card.Body>
          </Card>
          <UploadPage />
        </div>
      )}
    </Container>
  );
};

export default Instruction;
