import React, { useState } from "react";
import { Button, Container, Row, Col } from "react-bootstrap";
import UploadPage from "./UploadPage";
import Sample from "./Sample";

const GetStarted = () => {
  const [showInstruction, setShowInstruction] = useState(false);
  const [no, setNo] = useState(false);
  const [showSample, setShowSample] = useState(false);
  const [noButtonDisplay, setNoButtonDisplay] = useState("inline");
  const [okButtonDisplay, setOkButtonDisplay] = useState("inline");
  const [noButtonColor, setNoButtonColor] = useState("light");

  const handleOkButton = () => {
    setShowInstruction(true);
    setNoButtonDisplay("none");
  };

  const handleNoButton = () => {
    setNo(true);
    setOkButtonDisplay("none");
    setNoButtonColor("dark");
  };

  const handleSampleButton = () => {
    setShowSample(true);
  };

  return (
    <div>
      {showInstruction ? (
        <UploadPage />
      ) : showSample ? (
        <Sample />
      ) : (
        <Container className="text-left">
          <Row>
            <p>
              To get started, I will need to see your Netflix viewing history.
              Don't worry, I will just take a look at what you watched. I won't
              post or change anything.
            </p>
          </Row>
          <Row className="mb-3">
            <Col>
              <Button
                variant="light"
                onClick={handleOkButton}
                style={{ display: okButtonDisplay }}
              >
                Got it
              </Button>
            </Col>
          </Row>
          <Row className="mb-4">
            <Col>
              <Button
                variant={noButtonColor}
                onClick={handleNoButton}
                style={{ display: noButtonDisplay }}
              >
                No I don't want to share my view history
              </Button>
            </Col>
          </Row>
          {no && (
            <div>
              <p>
                I can't judge your Netflix without seeing your view history. I
                guess you can see how great our analysis could be using our
                sample data.....
              </p>
              <Button onClick={handleSampleButton}>
                Show me sample analysis
              </Button>
              <p>Or.....</p>
              <Button
                onClick={handleOkButton}
                style={{ display: okButtonDisplay }}
              >
                I change my mind
              </Button>
            </div>
          )}
        </Container>
      )}
    </div>
  );
};

export default GetStarted;
