import React from "react";
import { Line } from "react-chartjs-2";
import moment from "moment";
import { Card } from "react-bootstrap";

export default function ViewCount(props) {
  const { result } = props;

  //convert data object to arrays
  const resultsArr = Object.entries(result);

  //define yValues & xValues to be used with your chart
  const yValues = resultsArr.map((data) => data[1]);
  const xValues = resultsArr.map((data) => moment(data[0]).format("MMM YY"));

  //main data set for Graph
  const resultData = {
    labels: xValues,
    datasets: [
      {
        labels: "Your View Count",
        data: yValues,
        backgroundColor: "rgba(87, 121, 234, 0.6)",
        borderColor: "rgba(87, 121, 234, 0.6)",
        pointHoverRadius: 20,
        pointHoverBorderWidth: 5,
      },
    ],
  };

  //option for Graph
  const options = {
    maintainAspectRatio: false,
    // title: {
    //   display: true,
    //   text: "How many times did you watch Netflix overall?",
    //   fontSize: 20,
    //   fontColor: "rgb(224, 228, 228)",
    // },
    legend: {
      display: false,
    },
    scales: {
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: "total view count (per watch event)",
            fontColor: "rgb(224, 228, 228)",
          },
          ticks: {
            fontColor: "rgb(224, 228, 228)",
          },
        },
      ],
      xAxes: [
        {
          ticks: {
            fontColor: "rgb(224, 228, 228)",
          },
        },
      ],
    },
  };
  return (
    <Card
      style={{
        background: "rgb(34, 36, 41)",
        boxShadow: "0px 0px 20px 10px rgba(100, 100, 100, .3)",
        maxWidth: "600px",
        height: "680px",
        borderRadius: "10px",
        overflow: "hidden",
        padding: "2em",
        margin: "1em",
        color: "rgb(224, 228, 228)",
        display: "flex",
        justifyContent: "space-around",
      }}
    >
      <Card.Title style={{ textAlign: "center", fontSize: 30 }}>
        How many times did you watch Netflix overall?{" "}
      </Card.Title>
      <div>
        <Line data={resultData} options={options} width={600} height={400} />
      </div>
    </Card>
  );
}
