import React, { useState } from "react";
import { Container, Col, Row, Badge, Card } from "react-bootstrap";
import Typewriter from "typewriter-effect";
import ShowsCount from "./resultsComponent/ShowsCount";
import ViewCount from "./resultsComponent/ViewCount";
import GenresCount from "./resultsComponent/GenresCount";
import PopularityCount from "./resultsComponent/PopularityCount";
import RunTime from "./resultsComponent/RunTime";

const showData = { "We Bare Bears": 100 };
const genreData = {};
const timeData = {};
const popularityData = {};

const Sample = () => {
  const [done, setDone] = useState(false);

  const handleDone = () => {
    setDone(true);
  };
  return (
    <Container className="text-center">
      <Container className="text-center" style={{ height: "100vh" }}>
        <h1 className="netflix-red mt-5 mb-5">
          Your Netflix Analysis <Badge variant="warning">Example</Badge>
        </h1>
        <Row className="justify-content-center">
          <Col>
            <Typewriter
              onInit={(typewriter) => {
                typewriter
                  .typeString("Analyzing your fake data...")
                  .pauseFor(2500)
                  .deleteAll()
                  .typeString("Generating your fake score...")
                  .pauseFor(2500)
                  .deleteAll()
                  .typeString("Done.")
                  .pauseFor(2500)
                  .callFunction(() => {
                    handleDone();
                  })
                  .start();
              }}
              options={{
                deleteSpeed: 30,
              }}
            />
          </Col>
        </Row>
        {done && (
          <Row className="justify-content-center mt-5">
            <h2>Your score was 30 !</h2>
          </Row>
        )}
      </Container>
      <Container className="text-center">
        <Card className="text-dark mb-2">
          <Card.Title>Top Shows</Card.Title>
          <ShowsCount result={showData} />
        </Card>
        <Card className="text-dark mb-2">
          <Card.Title>Top Genre</Card.Title>
        </Card>
        <Card className="text-dark mb-2">
          <Card.Title>View Time</Card.Title>
        </Card>
        <Card className="text-dark mb-2">
          <Card.Title>Popularity</Card.Title>
        </Card>
      </Container>
    </Container>
  );
};

export default Sample;
