import React, { useState } from "react";
import { Card } from "react-bootstrap";

const Instruction = () => {
  return (
    <Card>
      <Card.Body>
        <Card.Text className="text-dark">
          <ol>
            <li>
              From a web browser, go to your
              <a href="https://www.netflix.com/YourAccount"> Account </a> page.
            </li>
            <li>
              Open the Profile & Parental Controls settings for the profile you
              want to see.
            </li>
            <li>Open Viewing activity. </li>
            <li>If you see a limited list, use the Show More button.</li>
          </ol>
        </Card.Text>
      </Card.Body>
    </Card>
  );
};

export default Instruction;
