import React from "react";
import ChatbotPage from "./chatComponent/ChatbotPage";
import Typewriter from "typewriter-effect";
import Instruction from "./Instruction";
import { InputGroup } from "react-bootstrap";

class UploadPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      result: {},
      loaded: false,
      Loading: false,
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  async handleSubmit(e) {
    e.preventDefault();
    console.log(e.target);
    this.setState({ loading: true, loaded: false, result: {} });
    const data = new FormData();
    data.append("submission", e.target[0].files[0]);
    const res = await fetch("http://127.0.0.1:3145/handleUpload", {
      method: "POST",
      headers: {
        "Access-Control-Allow-Origin": "*",
      },
      body: data,
    });
    var jsonRes = await res.json();
    console.log(jsonRes);
    this.setState({ loaded: true, result: jsonRes, loading: false });
  }

  render() {
    return (
      <div>
        {this.state.loaded ? (
          <ChatbotPage result={this.state.result} />
        ) : this.state.loading ? (
          <div>
            <img
              alt="loading"
              src="https://data.whicdn.com/images/325605417/original.gif"
            />
            <Typewriter
              options={{
                delay: 20,
                deleteSpeed: 10,
                strings: [
                  "Analyzing your watching history...",
                  "lol",
                  "omg",
                  "okay hold up",
                ],
                autoStart: true,
              }}
            />
          </div>
        ) : (
          <div>
            <div
              style={{
                height: "100vh",
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <p className="mb-5">
                Please follow below instruction below to download your viewing
                history <br />
                from your Netflix account and upload it here
              </p>
              <form
                action="/handleUpload"
                method="post"
                encType="multipart/form-data"
                onSubmit={this.handleSubmit}
              >
                Choose the file: <input type="file" name="submission" /> <br />
                <input type="submit" value="Upload" />
              </form>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="50"
                height="50"
                fill="currentColor"
                class="bi bi-chevron-compact-down"
                viewBox="0 0 16 16"
              >
                <path
                  fill-rule="evenodd"
                  d="M1.553 6.776a.5.5 0 0 1 .67-.223L8 9.44l5.776-2.888a.5.5 0 1 1 .448.894l-6 3a.5.5 0 0 1-.448 0l-6-3a.5.5 0 0 1-.223-.67z"
                />
              </svg>
            </div>
            <Instruction />
          </div>
        )}
      </div>
    );
  }
}
export default UploadPage;
